INSERT IGNORE INTO `#__content` (`id`, `asset_id`, `title`, `alias`, `introtext`, `fulltext`, `state`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`, `featured`, `language`, `xreference`) VALUES
(1, 89, '管理者用コンポーネント', '管理者用コンポーネント', '<p>すべてのコンポーネントは、サイトの管理画面エリアでも使用されています。ここに掲載しているものに加えて、サイト側には表示されませんが、しっかりと役目を果たしているコンポーネントもあります。多くのユーザにとって、もっとも重要なコンポーネントは、</p>\r\n<ul>\r\n<li>メディア管理</li>\r\n<li>エクステンション管理</li>\r\n<li>メニュー管理</li>\r\n<li>グローバル設定</li>\r\n<li>バナー</li>\r\n<li>リダイレクト</li>\r\n</ul>\r\n<hr title="メディア管理" alt="メディア管理" class="system-pagebreak" style="color: gray; border: 1px dashed gray;" />\r\n<p> </p>\r\n<h3>メディア管理</h3>\r\n<p>メディア管理コンポーネントはサイト全体のコンテンツに画像をアップロードし挿入することができます。必要に応じて、フラッシュアップローダを有効にし、複数の画像をアップロードすることができます。<a href="http://help.joomla.org/proxy/index.php?option=com_help&amp;keyref=Help16:Content_Media_Manager">ヘルプ</a></p>\r\n<hr title="エクステンション管理" alt="エクステンション管理" class="system-pagebreak" style="color: gray; border: 1px dashed gray;" />\r\n<h3>エクステンション管理</h3>\r\n<p>エクステンション管理はすべてのエクステンションをインストール、アップデート、アンインストール、および管理することができます。エクステンション管理は広範囲に再設計されましたが、コアインストールとアンインストールの機能はエクステンション管理はすべてのエクステンションをインストール、アップデート、アンインストール、および管理することができます。エクステンション管理はJoomla! 1.6のために広範囲に再設計されましたが、コアインストールとアンインストールの機能はJoomla 1.5のと同じまま残っています。<a href="http://help.joomla.org/proxy/index.php?option=com_help&amp;keyref=Help16:Extensions_Extension_Manager_Install">ヘルプ</a></p>\r\n<hr title="メニュー管理" alt="メニュー管理" class="system-pagebreak" style="color: gray; border: 1px dashed gray;" />\r\n<h3>メニュー管理</h3>\r\n<p>メニュー管理はサイトに表示されるメニューを作成することができます。また特定のメニューリンクにモジュールやテンプレートスタイルを割り当てることができます。<a href="http://help.joomla.org/proxy/index.php?option=com_help&amp;keyref=Help16:Menus_Menu_Manager">ヘルプ</a></p>\r\n<hr title="グローバル設定" alt="グローバル設定" class="system-pagebreak" style="color: gray; border: 1px dashed gray;" />\r\n<h3>グローバル設定</h3>\r\n<p>グローバル設定はサイト管理者が、サーチエンジンフレンドリURLが有効になっているか、サイトのメタデータ（検索エンジンやインデクサで使用される説明テキスト）、およびその他の機能を設定する場所です。多くの初歩ユーザーは単に標準の設定をままにして始めるのが良い方法です。とはいえ、サイトを公開する準備が整ったらそのコンテンツに見合ったメタデータに変更する必要があります。<a href="http://help.joomla.org/proxy/index.php?option=com_help&amp;keyref=Help16:Site_Global_Configuration">ヘルプ</a></p>\r\n<hr title="バナー" alt="バナー" class="system-pagebreak" style="color: gray; border: 1px dashed gray;" />\r\n<h3>バナー</h3>\r\n<p>バナーコンポーネントはモジュール内のローテーション画像を表示する簡単な方法を提供します。広告を希望する場合は、画像が何回表示されクリックされたかの回数を追跡することができます。<a href="http://help.joomla.org/proxy/index.php?option=com_help&amp;keyref=Help16:Components_Banners_Banners_Edit">ヘルプ</a></p>\r\n<hr title="リダイレクト" class="system-pagebreak" />\r\n<h3><br />リダイレクト</h3>\r\n<p>リダイレクトコンポーネントは ページが見つかりませんでした（404）エラーを引き起こす壊れたリンクを管理します。有効の場合、壊れたリンクを特定のページにリダイレクトすることができます。また、URLの変更に関連するリンクの移行の管理に使用することができます。<a href="http://help.joomla.org/proxy/index.php?option=com_help&amp;keyref=Help16:Components_Redirect_Manager">ヘルプ</a></p>', '', 1, 21, '2011-01-01 00:00:01', 42, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2011-01-01 00:00:01', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":"","urlatext":"","targeta":"","urlb":"","urlbtext":"","targetb":"","urlc":"","urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 1, 7, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
(2, 90, 'アーカイブモジュール', 'アーカイブモジュール', '<p>このモジュールは、アーカイブされた記事を含む暦月の一覧が表示します。記事をアーカイブの状態に変更後、この一覧は自動的に生成されます。<a href="http://help.joomla.org/proxy/index.php?option=com_help&amp;keyref=Help16:Extensions_Module_Manager_Articles_Archive" title="Archive Module">ヘルプ</a></p>\r\n<div class="sample-module">{loadmodule articles_archive,Archived Articles}</div>', '', 1, 64, '2011-01-01 00:00:01', 42, 'Joomla', '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '2011-01-01 00:00:01', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","page_title":"","alternative_readmore":"","layout":""}', 1, 5, 'modules, content', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
